INSERT INTO `tagroup_vi_leechnews_logs` VALUES (1,1,1,1,30,'Vườn hoa phật giáo_Phật học - Nguyên cứu<br/>Không lấy được nội dung chi tiết( <a href=\"http://vuonhoaphatgiao.com/phat-hoc/nguyen-cuu/nen-lua-chon-mot-ton-giao-chan-chinh-cho-chinh-minh/\" title=\"Xem bài viết gốc\" target=\"_blank\">Xem bài viết gốc</a> )<br/>Không lấy được nội dung chi tiết( <a href=\"http://vuonhoaphatgiao.com/phat-hoc/nguyen-cuu/nen-lua-chon-mot-ton-giao-chan-chinh-cho-chinh-minh/\" title=\"Xem bài viết gốc\" target=\"_blank\">Xem bài viết gốc</a> )',1473110246),(2,1,1,1,30,'Tin Phật Giáo_Phật học - Nguyên cứu<br/>Không phân tích được Khối bao ngoài nhóm tin',1473110305),(3,1,1,1,1,'Vườn hoa phật giáo_Phật học - Nguyên cứu',1473110353),(4,1,1,1,1,'Vườn hoa phật giáo_Phật học - Nguyên cứu',1473110452),(5,1,1,1,1,'Vườn hoa phật giáo_Phật học - Nguyên cứu',1473110576),(6,1,1,1,1,'Vườn hoa phật giáo_Phật học - Nguyên cứu',1473110606),(7,1,1,1,1,'Vườn hoa phật giáo_Phật học - Nguyên cứu',1473110717),(8,1,1,1,2,'Vườn hoa phật giáo_Phật học - Nguyên cứu<br/>Hoàn thành(Chờ kiểm duyệt)',1473110728),(9,1,1,1,2,'Vườn hoa phật giáo_Phật học - Nguyên cứu<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)',1473110881),(10,2,1,1,1,'Vườn hoa phật giáo_Phật học Thiền tông',1473110973),(11,3,1,1,2,'Vườn hoa phật giáo_Chùa trên thế giới<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)',1473111129),(12,4,1,1,2,'Vườn hoa phật giáo_Các chùa trong nước<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)<br/>Hoàn thành(Chờ kiểm duyệt)',1473111508),(13,1,1,1,2,'Vườn hoa phật giáo_Phật học - Nguyên cứu<br/>Hoàn 