INSERT INTO `tagroup_vi_news_19` VALUES (63,19,'19',0,2,'',0,1472170537,1472320083,1,1472170500,0,2,'Phật không thấy ai là kẻ thù','phat-khong-thay-ai-la-ke-thu','Đức Phật của chúng ta ngày xưa cũng bị nhiều người vu oan hủy nhục trước mặt vua quan, thần dân thiên hạ. Khi bị cô gái giả làm bụng bầu bêu xấu trước mặt đại chúng khi đang thuyết pháp Ngài vẫn an nhiên, không lời đính chính, minh oan cho mình.','2016_08/thumb_160_Phatgiao-org-vn-phat-khong-thay-ai-la-ke-thu01.jpg','Phật không thấy ai là kẻ thù',2,1,'4',1,26,0,0,0),(64,19,'19',0,2,'',0,1472170583,1472319804,1,1472170560,0,2,'Sau thời Chánh pháp','sau-thoi-chanh-phap','Đời tương lai, sẽ có Tỳ-kheo cạo bỏ râu tóc mà tập theo gia nghiệp...','2016_08/thumb_160_phatgiao-org-vn-sau-thoi-chanh-phap.jpg','Sau thời Chánh pháp',2,1,'4',1,26,0,0,0),(65,19,'19',0,2,'',0,1472170561,1472319966,1,1472170560,0,2,'Thế Tôn “chẳng nói tới người này”','the-ton-chang-noi-toi-nguoi-nay','Xả ly tham ái là một trong những nội dung tu học quan trọng trong giáo pháp của Thế Tôn. Để có sức khỏe và phương tiện tu học, người tu cũng rất cần một số vật dụng tối thiểu như thực phẩm, y phục, sàng tọa (giường tòa) và thuốc men. Vấn đề là tuy có thọ dụng mà phải giác tỉnh để không dính mắc, chẳng khởi tham ái, và đây cũng là điều khó làm.','2016_08/thumb_160_xa-ly-tham-ai.jpg','Thế Tôn “chẳng nói tới người này”',2,1,'4',1,24,0,0,0),(66,19,'19',0,2,'',0,1472170577,1472319885,1,1472170560,0,2,'Đặt tâm đúng hướng','dat-tam-dung-huong','Ngày nay, người tu tuy nhiều nhưng người thành tựu đạo nghiệp Thánh hiền thì không nhiều. Vậy nên “đặt tâm đúng hướng” cũng là một trong những vấn đề quan trọng mà mỗi người con Phật cần suy ngẫm và điều chỉnh để vượt thoát phiền não, thăng hoa tâm linh, thành tựu giải thoát, Niết-bàn.','2016_08/thumb_160_3433136680_06fc254fbb_o.jpg','Đặt tâm đúng hướng',2,1,'4',1,24,0,0,0),(67,19,'19',0,2,'',0,1472170555,1472320005,1,1472170500,0,2,'Làm giàu như thế nào để không mất phước báu?','lam-giau-nhu-the-nao-de-khong-mat-phuoc-bau','Việc thành bại trong kinh doanh không chỉ do kinh nghiệm, do linh hoạt, nhìn xa trông rộng... mà còn có yếu tố quan trọng là tâm đức và phước báu.','2016_08/thumb_160_Phatgiao-org-vn-lamgiaunhuthenaodekhongmatphuocbau-1.jpg','Làm giàu như thế nào để không mất phước báu?',2,1,'4',1,25,0,0,0),(68,19,'19',0,2,'',0,1472170577,1472319923,1,1472170560,0,2,'Kinh đắc quả khi từ trần và kinh tái sinh như lửa theo gió','kinh-dac-qua-khi-tu-tran-va-kinh-tai-sinh-nhu-lua-theo-gio','Tôi từng nghe rằng, có một lần Đức Thế Tôn đang ở gần Kosambi, nơi tu viện của Ghosita. Lúc đó, nội cung hoàng cung của Quốc Vương Udena bị cháy rụi, và 500 phụ nữ, dẫn đầu bởi Sāmāvatī, đã chết.','2016_08/thumb_160_Phatgiao-org-vn-kinh-dac-qua-khi-tu-tran-va-kinh-tai-sinh-nhu-lua-theo-gio01.jpg','Kinh đắc quả khi từ trần và kinh tái sinh như lửa theo gió',2,1,'4',1,23,0,0,0),(69,19,'19',0,2,'',0,1472170582,1472319843,1,1472170560,0,2,'Ni giới và những lời Phật dạy','ni-gioi-va-nhung-loi-phat-day','Tu sĩ hay cư sĩ đều được nương tựa Tam Bảo, cùng học và hành chung một nguồn Chánh pháp, và tất cả đều có điều kiện để đắc Thánh quả, Tâm và Tuệ giải thoát.','2016_08/thumb_160_Ni-gioi-va-nhung-loi-Phat-day.jpg','Ni giới và những lời Phật dạy',2,1,'4',1,22,0,0,0),(70,19,'19',0,2,'',0,1472170538,1472320048,1,1472170500,0,2,'Cái gì là bản lai diện mục của Tâm?','cai-gi-la-ban-lai-dien-muc-cua-tam','&quot;Nếu mắt ông vừa thấy, tâm ông liền biết, thì thân ông và tâm không thể rời nhau được. Như thế thì ông nói: Tâm ở ngoài thân cũng không phải.&quot;','2016_08/thumb_160_Phatgiao-org-vn-cai-gila-ban-lai-dien-muc-cua-tam01.jpg','Cái gì là bản lai diện mục của Tâm?',2,1,'4',1,25,0,0,0),(71,19,'19',0,2,'',0,1472170533,1472320122,1,1472170500,0,2,'Tỉnh thức và cơn mê','tinh-thuc-va-con-me','Phàm những sự thống khổ của muôn loài nhiều đến vô lượng. Tóm lại thời có tám điều: Khổ khi sinh ra, khổ khi già yếu, khổ lúc bệnh, khổ về sự chết, khổ vì phải ly biệt người thân mến, khổ vì gặp phải kẻ oán thù, khổ vì không được toại vọng, khổ vì thân tâm đòi hỏi phóng túng.','2016_08/thumb_160_Phatgiao-org-vn-tinh-thuc01.jpg','Tỉnh thức và cơn mê',2,1,'4',1,26,0,0,0);
/*!40000 ALTER TABLE `tagroup_vi_news_19` ENABLE KEYS */;
UNLOCK TABLES;

