INSERT INTO `tagroup_vi_news_20` VALUES (24,20,'20',0,2,'',0,1472080113,1472169830,1,1472080080,0,2,'Người dân Tây Tạng bị người Hán lấy đất làm du lịch','nguoi-dan-tay-tang-bi-nguoi-han-lay-dat-lam-du-lich','Người dân Tây Tạng ở một thị trấn Học viện Phật giáo Tây Tạng lớn nhất thế giới, một trung tâm học Phật nổi tiếng hiện nay đã bị phá hủy, và Chính quyền Bắc Kinh đã đuổi họ ra khỏi nơi cư trú để khuếch trương hệ thống giao thông cho một quần thể du lịch, người Tây Tạng đã được chi trả một khoản bồi thường theo mã thông báo, trong khi đó những người từ chối, bị buộc phải rời khỏi nơi cư trú.','2016_08/thumb_160_phatgiao-org-vnnguoidanTayTangbinguoiHanduoiTin-PG-Tay-Tang.jpg','Người dân Tây Tạng bị người Hán lấy đất làm du lịch',1,1,'4',1,54,0,0,0),(25,20,'20',0,2,'',0,1472080114,1472169841,1,1472080080,0,2,'Hoa Kỳ&#x3A; 72 nhà lập pháp yêu cầu Tổng thống hỗ trợ công khai cho đức Đạt Lai Lạt Ma','hoa-ky-72-nha-lap-phap-yeu-cau-tong-thong-ho-tro-cong-khai-cho-duc-dat-lai-lat-ma','Bức Thư yêu cầu Tổng thống Hoa Kỳ Barack Obama công khai và thường xuyên kêu gọi chính quyền Trung Quốc thả ngay lập tức và vô điều kiện đến tất cả tù nhân lương tâm chính trị Tây Tạng.','2016_08/thumb_160_Phatgiao-org-vn-hoa-ki-cac-nha-lap-phap-yeu-cau-tong-thong-obama-ho-tro-cong-khai-cho-duc-dat-lai-lat-ma01.jpg','Hoa Kỳ&#x3A; 72 nhà lập pháp yêu cầu Tổng thống hỗ trợ công khai cho đức Đạt Lai Lạt Ma',1,1,'4',1,29,0,0,0),(26,20,'20',0,2,'',0,1472080121,1472169850,1,1472080080,0,2,'Trung Quốc&#x3A; Ban hành sổ tay kiểm soát tăng sĩ PG Tây Tạng','trung-quoc-ban-hanh-so-tay-kiem-soat-tang-si-pg-tay-tang','Ngày 28/07/2016, chính quyền Trung Quốc, thị trấn Ngaba ban hành một cuốn sổ tay với nội dung các nghị định, hình sự hóa các hình thức biểu tình ôn hòa, diễu hành im lặng, tự thiêu và việc sử dụng chân dung đức Đạt Lai Lạt Ma trong biểu tình, bành trướng Bắc Kinh cho rằng đây là “những hành động phá vỡ sự ổn định xã hội”.','2016_08/thumb_160_Trung-Quoc-Chinh-quyen-ban-hanh-so-tay-kiem-soat-tang-si-PG-Tay-Tang-1.jpg','Trung Quốc&#x3A; Ban hành sổ tay kiểm soát tăng sĩ PG Tây Tạng',1,1,'4',1,66,0,0,0),(27,20,'20',0,2,'',0,1472080147,1472169899,1,1472080140,0,2,'Myanmar&#x3A; Các nhà lãnh đạo tôn giáo cầu nguyện cho tương lai thế hệ trẻ','myanmar-cac-nha-lanh-dao-ton-giao-cau-nguyen-cho-tuong-lai-the-he-tre','Thông tin từ Văn phòng Tổng thống Myanmar cho biết Hội nghị hòa bình liên bang Myanmar, hay còn gọi là Hội nghị Panglong thế kỷ 21, sẽ được chính phủ nước này tổ chức vào ngày 31/08 tới, nhằm quy tụ tất cả các nhóm vũ trang sắc tộc trên toàn quốc để thảo luận về việc chấm dứt các xung đột sắc tộc và xây dựng nhà nước liên bang.','2016_08/thumb_160_Phatgiao-org-vn-myanmar-cac-nha-lanh-dao-ton-giao-cau-nguyen1.jpg','Myanmar&#x3A; Các nhà lãnh đạo tôn giáo cầu nguyện cho tương lai thế hệ trẻ',1,1,'4',1,63,0,0,0),(28,20,'20',0,2,'',0,1472080094,1472169863,1,1472080080,0,2,'Ucraina, CH Séc, Ba Lan&#x3A; Kiều bảo tổ chức lễ Vu lan báo hiếu','ucraina-ch-sec-ba-lan-kieu-bao-to-chuc-le-vu-lan-bao-hieu','Chiều ngày 17/07/Bính Thân (19/8/2016) chùa Trúc Lâm Kharkov đã tổ chức lễ Vu lan báo hiếu PL.2560 – DL.2016. Đông đảo các gia đình phật tử và bà con cộng đồng từ các thành phố Kharkov, Kiev, Odessa và các địa phương khác của Ucraina đã về dự lễ.','2016_08/thumb_160_Phatgiao-org-ucraina-CH-Sec-Ba-Lan-to-chuc-le-vu-lan-bao-hieu07.jpg','Ucraina, CH Séc, Ba Lan&#x3A; Kiều bảo tổ chức lễ Vu lan báo hiếu',1,1,'4',1,26,0,0,0),(29,20,'20',0,2,'',0,1472080107,1472169874,1,1472080080,0,2,'Campuchia&#x3A; 3 vị sư trẻ bị bắt vì tàng trữ và sử dụng ma túy','campuchia-3-vi-su-tre-bi-bat-vi-tang-tru-va-su-dung-ma-tuy','Ngày 12/06/2016, ba vị sư trẻ độ tuổi từ 17 đến 21 tuổi đã bị cảnh sát bắt vì bị cáo buộc tàng trữ và sử dụng ma túy, và áp tải đến tòa án tỉnh Svay Rieng, một tỉnh Đông Nam của Campuchia (Phía Đông và phía Nam giáp giới với tỉnh Long An và Tây Ninh của Việt Nam).','2016_08/thumb_160_phatgiao-org-vnTin-Campuchia-1.jpg','Campuchia&#x3A; 3 vị sư trẻ bị bắt vì tàng trữ và sử dụng ma túy',1,1,'4',1,48,0,0,0),(30,20,'20',0,2,'',0,1472080141,1472081613,1,1472080140,0,2,'Ukraina&#x3A; Hàng ngàn kiều bào dự lễ Vu lan ở chùa Kharkov','ukraina-hang-ngan-kieu-bao-du-le-vu-lan-o-chua-kharkov','Chiều 19/08/2016 (17/07/Bính Thân), Chư Tăng phật tử chùa Trúc Lâm Kharkov đã long trọng cử hành đại lễ Vu lan Báo hiếu PL.2560 - DL.2016, đông đảo phật tử và bà con cộng đồng từ các địa phương từ Kharkov, Kiev, Odessa và các địa phương khác của Ukraina đã về dự lễ.','2016_08/thumb_160_Phatgiao-vndaileVuLanukraina7.jpg','Ukraina&#x3A; Hàng ngàn kiều bào dự lễ Vu lan ở chùa Kharkov',1,1,'4',1,64,0,0,0),(31,20,'20',0,2,'',0,1472080147,1472081335,1,1472080140,0,2,'Sri Lanka&#x3A; Lễ hội rước Xá lợi Răng Phật','sri-lanka-le-hoi-ruoc-xa-loi-rang-phat','Lễ hội rước xá lợi răng Phật - Esala Pehera ở Vương quốc Phật giáo Sri Lanka kết thúc thành công viên mãn vào hôm thứ Năm 18/08/2016.','2016_08/Phatgiao-vnThailanchumanhxuatgiagieoduyen61.jpg','',2,1,'4',1,62,1,0,0),(32,20,'20',0,2,'',0,1472080110,1472169887,1,1472080080,0,2,'Hình ảnh xuất gia gieo duyên tại tu viện Wat Phra Dhammakaya, Thái Lan','hinh-anh-xuat-gia-gieo-duyen-tai-tu-vien-wat-phra-dhammakaya-thai-lan','Xuất gia gieo duyên theo Phật giáo Bắc truyền (Đại thừa) thì thụ trì giới Bát Quan Trai trong một ngày một đêm, không cần cạo bỏ râu tóc và đắp áo cà sa, nhưng xuất gia gieo duyên theo Phật giáo Nam truyền (Theraveda) thì phải cạo bỏ râu tóc và đắp áo cà sa, thì thụ trì giới sa di.','2016_08/thumb_160_Phatgiao-vnThailanchumanhxuatgiagieoduyen61.jpg','Hình ảnh xuất gia gieo duyên tại tu viện Wat Phra Dhammakaya, Thái Lan',1,1,'4',1,54,0,0,0),(72,20,'20',0,2,'',0,1472170732,1472224735,1,1472170680,0,2,'Nepal&#x3A; Khôi phục lại ngôi cổ tự Phật giáo Tây Tạng thời trung cổ','nepal-khoi-phuc-lai-ngoi-co-tu-phat-giao-tay-tang-thoi-trung-co','Sâu thẳm trong trái tim của một ngôi cổ tự Phật giáo Tây Tạng vào thời trung cổ trong khu vực Upper Mustang từ xa của Nepal, cuộc chiến để khôi phục lại những bức tranh thiêng liêng và bảo tồn truyền thống văn hóa Phật giáo Tây Tạng thật cam go.','2016_08/thumb_160_Phatgiao-org-vn-Nepal-Khoi-phuc-lai-ngoi-co-tu-Phat-giao-Tay-Tang-thoi-trung-co-day-Himalaya.jpg','Nepal&#x3A; Khôi phục lại ngôi cổ tự Phật giáo Tây Tạng thời trung cổ',2,1,'4',1,48,0,0,0),(73,20,'20',0,2,'',0,1472170730,1472319307,1,1472170680,0,2,'Trung Quốc&#x3A; Khách du lịch tăng vọt có thể đe dọa nghệ thuật cổ PG hang đá Mạc Cao','trung-quoc-khach-du-lich-tang-vot-co-the-de-doa-nghe-thuat-co-pg-hang-da-mac-cao','Hang Mạc Cao, danh lam thánh tích Phật giáo nổi tiếng, đã tồn tại qua những thăng trầm trong nhiều thế kỷ. Những hang động trơ gan cùng tuế nguyệt bao thế kỷ của các thảm họa tự nhiên, kẻ cướp, nhà thám hiểm và xung đột vũ trang, bây giờ nó phải chịu sự tàn phá bởi mối đe dọa xảo quyệt hơn: Du lịch hiện đại.','2016_08/thumb_160_Phatgiao-org-vn-trung-quoc-khach-du-lich-tang-vot01.jpg','Trung Quốc&#x3A; Khách du lịch tăng vọt có thể đe dọa nghệ thuật cổ PG hang đá Mạc Cao',2,1,'4',1,38,0,0,0),(74,20,'20',0,2,'',0,1472170740,1472171560,1,1472170740,0,2,'Indonesia&#x3A; Tổng thống Joko Widodo chỉ đạo điều tra vụ phóng hỏa đốt chùa PG','indonesia-tong-thong-joko-widodo-chi-dao-dieu-tra-vu-phong-hoa-dot-chua-pg','Akarta, Medan – ngày 01/08/2016, Tổng thống Indonesia, Joko Widodo đã giao nhiệm vụ cho tướng Tito Karnavian, Tư lệnh Cảnh sát quốc gia Indonesia phải kiên quyết hành động trong vụ tấn công bạo loạn, phóng hỏa đốt nhiều ngôi Tự viện Phật giáo vào cuối tuần qua, tại thị trấn ven biển Tanjung Balai, tỉnh Bắc Sumatra.','2016_08/thumb_160_phatgiao-org-vnphonghoadottuvienphatgiaoTin-indonesia-1.jpg','Indonesia&#x3A; Tổng thống Joko Widodo chỉ đạo điều tra vụ phóng hỏa đốt chùa PG',2,1,'4',1,44,0,0,0),(75,20,'20',0,2,'',0,1472170681,1472319761,1,1472170680,0,2,'Ấn Độ&#x3A; Chư tăng ni Tây Tạng nghiên cứu Khoa học hiện đại và Phật giáo','an-do-chu-tang-ni-tay-tang-nghien-cuu-khoa-hoc-hien-dai-va-phat-giao','Dharamshala, 01/08/2016: Sở Tôn giáo và Văn hóa Tây Tạng (DRC)   phối hợp với Thư Viện Lưu Trữ Kinh Ðiển Tây Tạng (LTWA) chính thức khai mạc khóa thứ ba, Nghiên cứu Khoa học hiện đại và Phật giáo dành cho chư tăng ni Phật giáo Tây Tạng tại Trung tâm tiếp nhận Tây Tạng Khanyara, một vùng ngoại ô của Dharamshala, thuộc miền Bắc bang Himachal Pradesh, Ấn Độ.','2016_08/thumb_160_Phatgiao-org-vn-an-do-chu-tang-ni-tay-tang-nghien-cuu-khoa-hoc-hien-dai-va-phat-giao01.jpg','Ấn Độ&#x3A; Chư tăng ni Tây Tạng nghiên cứu Khoa học hiện đại và Phật giáo',2,1,'4',1,32,0,0,0),(76,20,'20',0,2,'',0,1472170698,1472319375,1,1472170680,0,2,'Indonesia&#x3A; Hàng trăm cảnh sát, binh sĩ quân đội dọn dẹp cơ sở Phật giáo bị đốt cháy','indonesia-hang-tram-canh-sat-binh-si-quan-doi-don-dep-co-so-phat-giao-bi-dot-chay','Sau vụ hỏa hoạn tại ngôi tự viện Phật giáo, ngày 31/07 hàng trăm binh sĩ cảnh sát quân đội dọn dẹp sạch sẽ cơ sở tôn giáo bị đốt cháy bởi cuộc bạo loạn phóng hỏa đốt một số tự viện Phật giáo tại thị trấn ven biển Tanjung Balai, Bắc Sumatra, vào tối thứ Sáu 29 Tháng 07, 2016. Hoạt động của các binh sĩ cảnh sát quân đội như người dân bình thường, họ tình nguyện làm việc nơi thờ tự tôn nghiêm như một sự gia tăng thêm niềm tin trong cuộc sống với tín ngưỡng tôn giáo.','2016_08/thumb_160_Phatgiao-org-vn-indonesia-hang-tram-binh-si-canh-sat-quan-doi-don-dep-co-so-ton-giao-bi-dot-chay03.jpg','Indonesia&#x3A; Hàng trăm cảnh sát, binh sĩ quân đội dọn dẹp cơ sở Phật giáo bị đốt cháy',2,1,'4',1,31,0,0,0),(77,20,'20',0,2,'',0,1472170687,1472319692,1,1472170680,0,2,'Indonesia&#x3A; Hội Sinh viên phật tử yêu cầu cảnh sát bảo vệ cơ sở Phật giáo','indonesia-hoi-sinh-vien-phat-tu-yeu-cau-canh-sat-bao-ve-co-so-phat-giao','Hội Sinh viên phật tử Indonesia rất xót xa khi kẻ xấu đã phóng hỏa đốt một số tự viện Phật giáo tại thị trấn ven biển Tanjung Balai, Bắc Sumatra, vào tối thứ Sáu 29 tháng 07 năm 2016.','2016_08/thumb_160_Phatgiao-org-vn-indonesia-hoi-sinh-vien-phat-tu01.jpg','Indonesia&#x3A; Hội Sinh viên phật tử yêu cầu cảnh sát bảo vệ cơ sở Phật giáo',2,1,'4',1,28,0,0,0),(78,20,'20',0,2,'',0,1472170703,1472319268,1,1472170680,0,2,'Ấn Độ&#x3A; Đức Đạt Lai Lạt Ma viếng thăm Thánh đường Hồi giáo Sunni và Shia','an-do-duc-dat-lai-lat-ma-vieng-tham-thanh-duong-hoi-giao-sunni-va-shia','Ngày 27/07/2016, nhân chuyến hành hương khu vực Ladakh, vùng đất nằm rìa Tây Tạng, thuộc bang Jammu và Kashmir, Ấn Độ, Đức Đạt Lai Lạt Ma viếng thăm Thánh đường Hồi giáo Sunni và Shia gần một Tu viện Phật giáo Tây Tạng.','2016_08/thumb_160_Phatgiao-org-vn-an-do-dat-lai-lat-ma-vieng-tham-thanh-duong-hoi-giao-sunni-va-shia03.jpg','Ấn Độ&#x3A; Đức Đạt Lai Lạt Ma viếng thăm Thánh đường Hồi giáo Sunni và Shia',2,1,'4',1,27,0,0,0),(79,20,'20',0,2,'',0,1472170721,1472319191,1,1472170680,0,2,'Indonesia&#x3A; Phần tử Hồi giáo cực đoan phóng hỏa đốt chùa Phật giáo','indonesia-phan-tu-hoi-giao-cuc-doan-phong-hoa-dot-chua-phat-giao','Khoảng 7 người bị Cảnh sát thẩm vấn tại một thị trấn duyên hải thuộc Tại tỉnh Bắc Sumatera, Indonesia, sau các vụ tấn công và đốt phá các ngôi tự viện Phật giáo trong đêm thứ Sáu, 29/07/2016 và bị bắt giữ vào ngày hôm sau 30/07.','2016_08/thumb_160_phatgiao-org-vnTin-indonesia-1.jpg','Indonesia&#x3A; Phần tử Hồi giáo cực đoan phóng hỏa đốt chùa Phật giáo',2,1,'4',1,29,0,0,0),(80,20,'20',0,2,'',0,1472170696,1472319638,1,1472170680,0,2,'Trung Quốc&#x3A; Kỷ niệm 100 năm ngày sinh cố trưởng lão HT.Chân Thiền','trung-quoc-ky-niem-100-nam-ngay-sinh-co-truong-lao-ht-chan-thien','Ngày 27/07/2016, tại Ngọc Phật thiền tự, Tp.Thượng Hải đã cử hành lễ kỷ niệm 100 năm ngày sinh cố trưởng lão Hòa thượng Chân Thiền  (1916 - 1995) , vị cao tăng mô phạm trác việt, nguyên Phương trượng trụ trì Ngọc Phật thiền tự, Hội trưởng Hiệp hội Phật giáo Thượng Hải, Phó Hội trưởng Hiệp hội Phật giáo Trung Quốc.','2016_08/thumb_160_Phatgiao-org-vn-trung-quoc-ky-niem-100-nam-ngay-sinh-co-truong-lao-hoa-thuong-chan-thien02.jpg','Trung Quốc&#x3A; Kỷ niệm 100 năm ngày sinh cố trưởng lão HT.Chân Thiền',2,1,'4',1,21,0,0,0);
