INSERT INTO `tagroup_vi_news_21` VALUES (33,21,'21',0,2,'',0,1472082107,1472121903,1,1472082060,0,2,'Chủ tịch nước Trần Đại Quang đón tiếp các chức sắc tôn giáo','chu-tich-nuoc-tran-dai-quang-don-tiep-cac-chuc-sac-ton-giao','Nhân kỷ niệm 71 năm Cách mạng tháng Tám và Quốc khánh 2/9, sáng ngày 18/8/2016 (16/07/Bính Thân), tại Phủ Chủ tịch, Chủ tịch nước Trần Đại Quang đã có buổi gặp mặt 29 chức sắc, chức việc đại diện cho 17 tổ chức tôn giáo trong cả nước.','2016_08/thumb_160_ghchucmungchutich00002.jpg','Chủ tịch nước Trần Đại Quang đón tiếp các chức sắc tôn giáo',2,1,'4',1,50,0,0,0),(34,21,'21',0,2,'',0,1472082099,1472122229,1,1472082060,0,2,'Đại sứ Hoa Kỳ đưa hai con đến chùa tìm hiểu về lễ Vu Lan','dai-su-hoa-ky-dua-hai-con-den-chua-tim-hieu-ve-le-vu-lan','Trong dịp Vu Lan năm nay, Đại sứ Hoa Kỳ Ted Osius không về thăm mẹ được nên đã đến chùa Kim Liên (Hà Nội) để làm lễ Vu Lan báo hiếu gửi tới mẹ và người cha đã mất.','2016_08/thumb_160_phatgiao-org-vn_dai_su_my_du_vu_lan_9.jpg','Đại sứ Hoa Kỳ đưa hai con đến chùa tìm hiểu về lễ Vu Lan',2,1,'4',1,57,0,0,0),(35,21,'21',0,2,'',0,1472082127,1472124305,1,1472082120,0,2,'Hà Nội&#x3A; Đại sứ Việt Nam tại Ukraina thăm và lễ Phật tại chùa Quán Sứ','ha-noi-dai-su-viet-nam-tai-ukraina-tham-va-le-phat-tai-chua-quan-su','Nhân dịp Đại lễ Vu Lan báo hiếu, Đại sứ đặc mệnh toàn quyền Việt Nam tại Ukraina Nguyễn Minh Trí đã đến thăm và lễ Phật chùa Quán sứ.','2016_08/thumb_160_phatgiao-org-vn-dai-su-VN-tai-ucraina-le-Phat-chua-Quan-Su-004.jpg','Hà Nội&#x3A; Đại sứ Việt Nam tại Ukraina thăm và lễ Phật tại chùa Quán Sứ',2,1,'4',1,57,0,0,0),(36,21,'21',0,2,'',0,1472082130,1472124393,1,1472082120,0,2,'Hà Nội&#x3A; Họp báo về việc chiêm bái tôn tượng Phật Ngọc Hòa bình Thế giới tại Thái Nguyên','ha-noi-hop-bao-ve-viec-chiem-bai-ton-tuong-phat-ngoc-hoa-binh-the-gioi-tai-thai-nguyen','Sáng ngày 04/07/Bính thân (06/08/2016), tại chùa Quán Sứ (số 73 phố Quán Sứ, Hà Nội) đã diễn ra Họp báo lễ cung rước - an vị triển lãm Phật Ngọc Hòa bình Thế giới tại chùa Phủ Liễn (Tp.Thái Nguyên, tỉnh Thái Nguyên).','2016_08/thumb_160_phatgiao-org-vn-Hop-bao-Phat-ngoc-Thai-Nguyen-004.jpg','Hà Nội&#x3A; Họp báo về việc chiêm bái tôn tượng Phật Ngọc Hòa bình Thế giới tại Thái Nguyên',2,1,'4',1,58,0,0,0),(37,21,'21',0,2,'',0,1472082084,1472123259,1,1472082060,0,2,'Đoàn Bộ Lễ nghi và Tôn giáo Vương quốc Campuchia thăm Giáo hội PGVN','doan-bo-le-nghi-va-ton-giao-vuong-quoc-campuchia-tham-giao-hoi-pgvn','Chiều ngày 02/08/2016 đoàn Bộ Lễ nghi và Tôn giáo Vương quốc Campuchia do ngài HIM CHHEM, Ủy viên T.Ư Đảng nhân dân Campuchia , Bộ trưởng cao cấp kiêm Bộ trưởng Bộ Lễ nghi và Tôn giáo Vương quốc Campuchia dẫn đầu đã tới trụ sở T.Ư GHPGVN số 73 Phố Quán sứ thăm và làm việc.','2016_08/thumb_160_doantongiaocampuchia00005.jpg','Đoàn Bộ Lễ nghi và Tôn giáo Vương quốc Campuchia thăm Giáo hội PGVN',2,1,'4',1,58,0,0,0),(38,21,'21',0,2,'',0,1472082111,1472121638,1,1472082060,0,2,'Điện Phân ưu Chủ tịch Ủy ban Đoàn kết Công giáo việt Nam','dien-phan-uu-chu-tich-uy-ban-doan-ket-cong-giao-viet-nam','Được tin Linh mục Phero Nguyễn Công Danh, Phó Chủ tịch Ủy ban Trung ương Mặt trận Tổ quốc Việt Nam, Chủ tịch Ủy ban Đoàn kết Công giáo Việt Nam đã từ trần lúc: 21 giờ 15 phút ngày 27/7/2016, tại Bệnh viện Thống Nhất, Hòa thượng Thích Thiện Nhơn, Chủ tịch Hội đồng Trị sự Giáo hội Phật giáo Việt Nam đã gửi điện Phân ưu đến Ủy ban Đoàn kết Công giáo Việt Nam.','2016_08/thumb_160_hoa-sen.jpg','Điện Phân ưu Chủ tịch Ủy ban Đoàn kết Công giáo việt Nam',2,1,'4',1,46,0,0,0),(39,21,'21',0,2,'',0,1472082099,1472122541,1,1472082060,0,2,'Hà Nội&#x3A; Chấm giải Cuộc thi Sáng tác các tác phẩm về Đạo Hiếu','ha-noi-cham-giai-cuoc-thi-sang-tac-cac-tac-pham-ve-dao-hieu','Chiều ngày 16/06/Bính Thân (19/07/2016), Ban Giám khảo Cuộc thi sáng tác các tác phẩm Đạo hiếu đã tổ chức buổi chấm giải tại phòng họp Phân viện Nghiên cứu Phật học VN tại Hà Nội, chùa Quán Sứ.','2016_08/thumb_160_phatgiao-org-vn-Cham-giai-Cuoc-thi-sang-tac-cac-tac-pham-dao-Hieu-028.jpg','Hà Nội&#x3A; Chấm giải Cuộc thi Sáng tác các tác phẩm về Đạo Hiếu',2,1,'4',1,59,0,0,0),(40,21,'21',0,2,'',0,1472082096,1472123035,1,1472082060,0,2,'Phú Yên&#x3A; Khai mạc trại Vạn Hạnh khóa IV','phu-yen-khai-mac-trai-van-hanh-khoa-iv','Ngày 13/07/2016 tại Giảng đường chùa Bảo Tịnh (Tp.Tuy Hoà), Ban Hướng dẫn Phân Ban GĐPT T.Ư đã tổ chức lễ khai mạc &quot;Trại huấn luyện Huynh trưởng cấp 3 Vạn Hạnh khóa IV&quot; cho 277 Huynh trưởng trại sinh trúng cách bậc Lực IV sau 4 năm tu học (2011-2016)','2016_08/thumb_160_Phatgiao-org-vn-khai-mac-tra-van-hanh-khoa-iv07.jpg','Phú Yên&#x3A; Khai mạc trại Vạn Hạnh khóa IV',2,1,'4',1,41,0,0,0),(41,21,'21',0,2,'',0,1472082086,1472123122,1,1472082060,0,2,'Gs.Trịnh Xuân Thuận nói chuyện về khoa học vũ trụ và Phật giáo','gs-trinh-xuan-thuan-noi-chuyen-ve-khoa-hoc-vu-tru-va-phat-giao','Vừa qua, trong khuôn khổ chuyến về thăm quê và giới thiệu cuốn sách “Đối mặt với vũ trụ” (NXB Tri Thức), Gs.Trịnh Xuân Thuận- nhà thiên văn học nổi tiếng thế giới người Việt quốc tịch Hoa Kỳ đã có buổi nói chuyện tại Trung tâm Văn hóa Pháp 24. Tràng Tiền, Hà Nội. Trong một cử tọa kín đặc người, có rất nhiều bạn trẻ và là các phật tử làm cho buổi nói chuyện càng thêm hứng thú- kiến thức của khoa học và những cái thấy cái biết từ tuệ giác của Phật từ cách nay hơn 25 thế kỷ đã gặp gỡ.','2016_08/thumb_160_Phatgiao-vngiaouTrinhXuanThuankhoahocvaphatgiao1.jpg','Gs.Trịnh Xuân Thuận nói chuyện về khoa học vũ trụ và Phật giáo',2,1,'4',1,57,0,0,0),(90,21,'21',0,2,'',0,1472511776,1472723202,1,1472511720,0,2,'Ủy viên Bộ Chính trị Trần Quốc Vượng trồng cây lưu niệm tại chùa Tân Thanh','uy-vien-bo-chinh-tri-tran-quoc-vuong-trong-cay-luu-niem-tai-chua-tan-thanh','Nhân chuyến công tác tại Lạng Sơn, chiều ngày 24/8/2016, ông Trần Quốc Vượng - Ủy viên Bộ Chính trị, Chủ nhiệm Ủy ban Kiểm tra T.Ư đã đến chùa Tân Thanh tại cửa khẩu Tân Thanh (huyện Văn Lãng) dâng hương, lễ Phật và trồng cây lưu niệm.','2016_08/thumb_160_phatgiao-org-vn-Lang-son-chua-Thanh-1.jpg','Ủy viên Bộ Chính trị Trần Quốc Vượng trồng cây lưu niệm tại chùa Tân Thanh',2,1,'4',1,31,0,0,0),(91,21,'21',0,2,'',0,1472511802,1472722690,1,1472511780,0,2,'Phó Chủ tịch nước Đặng Thị Ngọc Thịnh về thăm Chùa Ba Vàng','pho-chu-tich-nuoc-dang-thi-ngoc-thinh-ve-tham-chua-ba-vang','Ngày 23/08/2016 (21/07/Bính Thân), Chư tăng, ni, phật tử chùa Ba Vàng vô cùng vinh dự được đón tiếp Phó Chủ tịch nước CHXHCN Việt Nam về thăm chùa, dâng hương và lễ Phật cầu Quốc thái dân an.','2016_08/thumb_160_Phatgiao-vnphochutichnuocthamchuaBavang6.jpg','Phó Chủ tịch nước Đặng Thị Ngọc Thịnh về thăm Chùa Ba Vàng',2,1,'4',1,32,0,0,0),(92,21,'21',0,2,'',0,1473109166,1473622793,1,1473109140,0,2,'Thủ tướng Ấn Độ thăm, lễ Phật tại chùa Quán Sứ','thu-tuong-an-do-tham-le-phat-tai-chua-quan-su','Chiều ngày 03/08/Bính Thân (03/09/2016), Thủ tướng Ấn Độ Narendra Modi đã có chuyến thăm, lễ Phật tại chùa Quán Sứ - Trụ sở Trung ương Giáo hội.','2016_09/thumb_160_phatgiao-org-vn-Thu-tuong-an-do-tham-chua-Quan-Su-105.jpg','Thủ tướng Ấn Độ thăm, lễ Phật tại chùa Quán Sứ',2,1,'4',1,32,0,0,0),(93,21,'21',0,2,'',0,1473109213,1473620453,1,1473109200,0,2,'Thủ tướng Ấn Độ sẽ thăm chùa Quán Sứ','thu-tuong-an-do-se-tham-chua-quan-su','Người đứng đầu Chính phủ của đất nước Phật giáo Ấn Độ sẽ đến thăm trụ sở Giáo hội Phật giáo Việt Nam trong chuyến thăm Hà Nội.','2016_09/thumb_160_phatgiao-org-vn-modi-phat.jpg','Thủ tướng Ấn Độ sẽ thăm chùa Quán Sứ',2,1,'4',1,30,0,0,0),(94,21,'21',0,2,'',0,1473109192,1473622440,1,1473109140,0,2,'Chiêm bái tôn tượng Phật ngọc tại chùa Dâu, Bắc Ninh','chiem-bai-ton-tuong-phat-ngoc-tai-chua-dau-bac-ninh','Tối ngày 24/07/Bính Thân (26/08/2016) chùa Dâu (xã Thanh Khương, huyện Thuận Thành) đã trang nghiêm  tổ chức Lễ khai mạc triển lãm, an vị tôn tượng Phật ngọc hòa bình  thế giới.','2016_09/thumb_160_tuPd017.jpg','Chiêm bái tôn tượng Phật ngọc tại chùa Dâu, Bắc Ninh',2,1,'4',1,35,0,0,0);
