LOCK TABLES `tagroup_vi_nhac_phat_giao_detail` WRITE;
/*!40000 ALTER TABLE `tagroup_vi_nhac_phat_giao_detail` DISABLE KEYS */;
INSERT INTO `tagroup_vi_nhac_phat_giao_detail` VALUES (1,'<div style=\"text-align: center;\">&nbsp;</div>\r\n\r\n<div style=\"text-align: justify;\">&nbsp;\r\n<div class=\"ckeditor-html5-audio\" style=\"text-align: center;\">\r\n<audio autoplay=\"autoplay\" controls=\"controls\" src=\"/uploads/nhac-phat-giao/2016_08/MucLienCuuMe-TheVu_4c8ba.mp3\">&nbsp;</audio>\r\n</div>\r\n\r\n<div style=\"text-align: center;\">&nbsp;</div>\r\n<br  />\r\nĐức Thế Tôn từ bi, xin chứng cho tâm thành, thương mẫu thân đọa đày, &nbsp;chịu ngục hình khổ đau triền miên, vô dáng nơi a tỳ,<br  />\r\nlinh hồn người đớn đau ngày đêm.<br  />\r\n<br  />\r\nThương mẹ hiền đọa đày, bao ngày đói khát, bát cơm thâm tình, lòng con hiếu xin dâng,&nbsp;để cho mẫu thân no lòng nhưng nào ngờ có ăn được đâu.<br  />\r\n<br  />\r\nCơm chưa đưa tới miệng đã hóa thành lửa than còn đâu.<br  />\r\n<br  />\r\nLòng con đau đớn xót xa, biết sao đáp đền thâm ân.<br  />\r\n<br  />\r\nCúi xin Đức Phật từ bi, ban pháp Phật nhiệm mầu huyền di, cứu mẹ thoát a tỳ khổ đau.<br  />\r\n<br  />\r\nƠi hỡi Mục Kiền Liên, ta cảm thương cho người. Đây pháp Vu Lan Bồn, đạo nhiệm mầu cứu cho mẹ cha.<br  />\r\n<br  />\r\nTa thuyết cho muôn người cùng thực hành báo ân mẹ cha.&nbsp;<br  />\r\n<br  />\r\nVu Lan Bồn chính là ngày rằm tháng bảy, chư tăng hội về mười phương oai đức vô biên.<br  />\r\n<br  />\r\nHãy mau chí tâm cúng dường và nguyện nhờ phước ơn mười phương, cứu cho mẫu thân của người thoát a tỳ khổ đau cầm giam.<br  />\r\n<br  />\r\nNhờ công ơn đức Thế Tôn, mẫu thân sinh về thiên cung, sống nơi cõi trời an vui, thoát a tỳ ngục hình khổ đau, Vu Lan Bồn nhiệm mầu huyền di.&nbsp;<br  />\r\n<br  />\r\nƠn sinh thành vô vàn to lớn, sánh như biển trời, lòng con ghi nhớ ai ơi. Giữ cho hiếu ân vuông tròn, luôn phụng thờ kính yêu mẹ cha.<br  />\r\n<br  />\r\nĐêm đêm thắp nhan nguyện cầu, cha mẹ hiền sống lâu cùng con.&nbsp;<br  />\r\n<br  />\r\nCòn khi cha mẹ mất đi, chắp tay lạy cầu mười phương, cứu cho linh hồn mẹ cha, thoát qua đọa đày khổ đau,&nbsp;vãng sanh về Di Đà Tây Phương, như Đức Mục Kiền Liên cứu mẹ hiền thoát vòng khổ đau</div>','',2,0,1,1,1,0),(2,'Tôi yêu chú tiểu ngây thơ<br  />\r\nbiết vì sao chú lại ở chùa<br  />\r\nlâu lâu thấy tiểu ngây ngô<br  />\r\ncầm trổi chà quét quét lá đa,<br  />\r\nTôi yêu chú đã xa cha&nbsp;<br  />\r\nxa mẹ hiền ngày đêm thương nhớ<br  />\r\nđôi khi thấy tiểu đâm chiêu<br  />\r\nmắt lệ nhòa vì không thuộc bài.<br  />\r\nđêm đêm chú tiểu ngân vang&nbsp;<br  />\r\nvới lời kinh trầm bổng du dương<br  />\r\nchú mang hết cả tâm tư<br  />\r\ntrái tâm từ cầu an cho đời<br  />\r\nai xui chú tiểu ngây thơ&nbsp;<br  />\r\nbiết tu hành giữa chốn không môn<br  />\r\ncâu kinh tiếng mỏ thâu đêm<br  />\r\nnghiêng nghiêng đầu êm êm mái chùa<br  />\r\nđời còn có biết bao sắc màu lam ấy<br  />\r\nngười người vui bên mái ấm gia đình<br  />\r\ncòn chú tiểu, đâu là chiếc lá vô sanh<br  />\r\nmà vượt qua gian khổ kiếp tu hành<br  />\r\ntôi yêu dáng tiểu ngây thơ<br  />\r\náo màu lam chú mặc ở chùa<br  />\r\nchén cơm với muối tương rau<br  />\r\nnhưng tấm lòng các chú thanh cao<br  />\r\nmong sao chú tiểu an vui&nbsp;<br  />\r\nbên mái chùa ngày đêm tinh tấn<br  />\r\nđể đem tiếng mỏ câu kinh<br  />\r\ntrái tâm từ cầu an cho đời','',2,0,1,1,1,0),(3,'<p data-max=\"auto\" data-min=\"300px\">Tết năm này zui thiệt là zui<br  />\r\nđón xuân di lặc thật là zui ghê<br  />\r\ntết năm này thật là an khang<br  />\r\nkính chúc may mắn an lành<br  />\r\n<br  />\r\ntết năm này ai cũng mong ước<br  />\r\ngiống ông di lặc miệng cười haha<br  />\r\ntết dâng tràn tình người bao la<br  />\r\nmừng năm mới chúc phúc cho mọi nhà<br  />\r\n<br  />\r\ntết về! tết tết về ! Ông di lặc cũng về<br  />\r\ngiúp mọi người sống yêu đời<br  />\r\nông zui cười hả hê<br  />\r\ntết về! tết tết về ! Bao tin lành cũng về<br  />\r\nkhắp phương trời mừng đón xuân về<br  />\r\nbên phật Di Lặc thảnh thơi</p>\r\n<br  />\r\n<a href=\"http://mp3.zing.vn/playlist/Nhac-Phat-Giao-hay-nhat-nuthan61089/IWB8I7E6.html?st=4#\">Đóng góp lyrics</a>','',2,0,1,1,1,0),(4,'<p data-max=\"auto\" data-min=\"300px\">Nam mô Bổn tôn Địa tạng Bồ Tát<br  />\r\nLòng từ bi vô lượng chứa chan hạnh lành<br  />\r\nLòng từ bi Bồ Tát cứu độ chúng sanh<br  />\r\nĐưa người qua bến an lành<br  />\r\nVới bao số kiếp độ sanh khỏi nạn<br  />\r\nĐịa tạng Bồ Tát ma ha tát<br  />\r\nTay cầm châu sáng, tay cầm gậy vàng<br  />\r\nDộng tan cửa ngục cứu toàn chúng sanh<br  />\r\nĐịa tạng Bồ Tát, với cả tấm lòng yêu thương chúng sanh<br  />\r\nBồ Tát phát nguyện địa ngục chưa trống thề không thành Phật<br  />\r\nNam mô Bổn tôn Địa tạng Bồ Tát<br  />\r\nNgày chúng sanh độ hết mới chứng Bồ đề<br  />\r\nLòng đại bi, đại nguyện, đại thánh, đại từ<br  />\r\nBổn tôn Địa tạng Bồ Tát<br  />\r\nHào quang soi khắp ba ngàn Đại thiên.</p>\r\n<br  />\r\n<a href=\"http://mp3.zing.vn/playlist/Nhac-Phat-Giao-hay-nhat-nuthan61089/IWB8I7E6.html?st=5#\">Đóng góp lyrics</a>','',2,0,1,1,1,0),(5,'Gieo hạt từ tâm, không ghen ghét hơn thua hận thù<br  />\r\nGieo hạt từ tâm, gieo yêu thương chan hòa muôn hướng<br  />\r\nCùng chia nhau hạnh phúc cùng chia sớt vui buồn<br  />\r\nĐem đến cho trần gian ngập tràn thương yêu<br  />\r\nGieo hạt từ tâm, đoạn trừ hết tham sân mê lầm<br  />\r\nGieo hạt từ tâm, cho trí tuệ rằng người thế giới<br  />\r\nĐể tâm ta bừng sáng lòng từ bi bao la cho thế gian bình yên sống trong an lành<br  />\r\nNam Mô Quan Thế Âm, Quan Thế Âm Bồ Tát, Nam Mô Quan Thế Âm, Quan Thế Âm Bồ Tát<br  />\r\nNương thuyền từ bến gió','',2,0,1,1,1,0),(6,'Nam Mô Dược Sư Lưu Ly Quang Vương Phật<br  />\r\nMột nén hương thơm con xin dâng lên Người Đức Phật Dược Sư.<br  />\r\nMười hai nguyện lớn của Người đem đến cho đời vạn niềm vui<br  />\r\nNhân gian bước qua bao la bể khổ, cho con bước qua bao bến mê đời.<br  />\r\n<br  />\r\nNam Mô Dược Sư Lưu Ly Quang Vương Phật<br  />\r\nNgười đã xua tan bao nhiêu nỗi ưu phiền khổ lụy đời con.<br  />\r\nTìm lên cõi phúc trong đời, quỳ dưới chân Ngài nguyện thành tâm<br  />\r\nCon xin phát nguyện nương thân cửa Phật, trăm năm sống vui trọn kiếp con người.<br  />\r\n<br  />\r\nPhật Dược Sư hào quang sáng ngời, hào quang sáng ngời.<br  />\r\nSoi đường chúng con vượt qua tâm tối<br  />\r\nPhật Dược Sư bàn tay nhiệm mầu xoa dịu nỗi đau<br  />\r\nNỗi đau tật bệnh xua tan lo buồn trầm luân thế gian.<br  />\r\n<br  />\r\nNam mô Dược Sư Lưu Ly Quang Vương Phật<br  />\r\nNgười đã khai tâm con tu thân tu đời tu đạo Từ Bi<br  />\r\nTrầm hương tỏa ngát Đất Trời, quỳ dưới chân Ngài Phật Dược Sư<br  />\r\nCon xin chấp tay con xin khấn nguyện,cho con sống vui trọn kiếp con người.','',2,0,1,1,1,0),(7,'Thấp thoáng sương kia đã tan rồi<br  />\r\nPhật là bình minh sáng bên đồi<br  />\r\nRíu rít chim tung cánh lưng trời<br  />\r\nPhật là cơn gió bay nghìn nơi<br  />\r\n<br  />\r\nNhững lúc hương xuân đến cho người<br  />\r\nPhật là muôn đóa hoa đẹp tươi<br  />\r\nTíu tít em thơ tiếng reo cười<br  />\r\nPhật là tia nắng mang niềm vui<br  />\r\n<br  />\r\nCon quỳ nơi đây, nguyện ước sâu xa<br  />\r\nDâng niềm tin yêu về đấng cao siêu<br  />\r\nĐêm dài vô minh, tìm thấy yêu bình<br  />\r\nTheo Phật đi trong ánh dương mùa xuân<br  />\r\n<br  />\r\nLời kinh đưa bước chân qua nghìn trùng<br  />\r\nHoa sen nở thắm tâm hồn bao dung<br  />\r\nPhật ân soi sáng trái tim dịu dàng<br  />\r\nAi có nghe chăng Pháp âm rền vang<br  />\r\n<br  />\r\nXin thương nhau&nbsp;<br  />\r\nCho Trái Đất vơi niềm đau<br  />\r\nCùng đi qua những trùng khơi<br  />\r\nHát reo mừng ngày mới<br  />\r\n<br  />\r\nĐem tin yêu<br  />\r\nLan đến vô tận nghìn nơi<br  />\r\nĐể con tim sẽ lắng sau<br  />\r\nBên Phật đài thiêng liêng<br  />\r\n<br  />\r\nLời 2:<br  />\r\nThấp thoáng trăng lên phía chân trời<br  />\r\nPhật là dòng sông nước trong lời<br  />\r\nLấp lánh mây bay đến xa vời<br  />\r\nPhật là mưa thắm tươi nghìn nơi<br  />\r\n<br  />\r\nNhững lúc hoa mai sắc khoe vàng<br  />\r\nPhật là hơi ấm xuân vừa sang<br  />\r\nRóc rách non cao suối êm đềm<br  />\r\nPhật là cây lá xanh triền miên<br  />\r\n<br  />\r\nCon quỳ nơi đây, nguyện ước bao la<br  />\r\nDâng tình yêu thương trải khắp muôn phương<br  />\r\nĐêm dài chơi vơi, tìm ánh mặt trời<br  />\r\nTheo Phật đi nơi nắng mai đẹp tươi<br  />\r\n<br  />\r\nLời kinh gieo xuống núi xanh cuộc đời<br  />\r\nNhư hoa nở thắm trên trời rơi rơi<br  />\r\nPhật cho ta những trái tim dịu dàng<br  />\r\nAi có nghe chăng Pháp âm rền vang<br  />\r\n<br  />\r\nXin thương nhau&nbsp;<br  />\r\nCho ước mơ muôn đời sau<br  />\r\nTrần gian qua hết niềm đau<br  />\r\nCõi thanh bình ngời sáng<br  />\r\n<br  />\r\nTa chung nhau&nbsp;<br  />\r\nCho nhứng ưu phiền lìa xa<br  />\r\nLòng ta như những đóa hoa<br  />\r\nBên Phật đài bao la','',2,0,1,1,1,0),(8,'Từ biệt cuộc đời cao sang ấm êm. Từ chối ngai vàng làm vua thế gian. Tình thê nhi đâu xá, tìm vui trong băng giá. Người ra đi xa không lời từ giã. Rồi bao năm dài phong sương nắng mưa. Rừng sâu miệt mài thành tâm tiến tu. Và đêm trăng thanh đó, bừng lên bao chân lý, ngàn muôn năm sau vẫn soi sáng đời.&nbsp;<br  />\r\nCa ngợi Đấng Thế Tôn, ca ngợi Đấng Chánh Giác, ta nguyện noi bước chân người. Ca ngợi Đấng Thế Tôn, ca ngợi Đấng Chánh Giác, chúng ta kết đoàn tiến lên. Phát tâm từ bi đi muôn nơi. Rắc gieo tình thương cho muôn phương. Đem lại bình an trên thế giới. Khắp nơi rực ánh Đạo vàng.<br  />\r\nCa ngợi Đấng Thế Tôn, ca ngợi Đấng Chánh Giác, ca ngợi Phật pháp muôn đời. Ca ngợi Đấng Thế Tôn, ca ngợi Đấng Chánh Giác, bốn phương ánh đạo sáng tươi.','',2,0,1,1,1,0),(9,'Chắp Tay Niệm Phật<br  />\r\nLâng lâng dòng suôí ngọt ngào, diệu vì hương ngát lôí vào cửa không<br  />\r\nSen tươi chẳng nhuốm bụi trần lân, lân hồn tỉnh thức chuông ngân nhẹ nhàng<br  />\r\nNhiệm huyền ánh sáng từ quang&nbsp;<br  />\r\nChấp tay niệm phật trút ngàn khổ đau<br  />\r\nChấp tay niệm Phật nhiệm màu<br  />\r\nThế tôn ân đức biển sâu bao đời<br  />\r\nCon thành tâm kính lạy ngài, chấp tay niệm Phật, hồn tươi trăng rằm<br  />\r\nNhư Lai huyền diệu pháp âm<br  />\r\nChấp tay niệm phật trù tâm an hoà<br  />\r\nNam mô bổn sư thích ca<br  />\r\nChấp tay niệm phật tâm hoa cúng dường<br  />\r\nChấp tay niệm phật 10 phương<br  />\r\nĐạo vàng chân lý mở đường độ sinh<br  />\r\nChấp tay niệm phật kính thành<br  />\r\nMở tâm hồi hướng sinh linh muôn loài<br  />\r\nNam mô bổn sư thích ca','',2,0,1,1,1,0),(10,'Chùa tôi vang tiếng chuông, chiều buông theo khói sương, trầm hương nương gió đưa hương thơm tỏa ngát.<br  />\r\nChùa tôi trong nắng mai, màu lam bay thướt tha, gặp nhau chắp tay A DI ĐÀ PHẬT, đêm trăng rằm phản chiếu lung linh ngàn đài sen tỏa sáng dưới bóng từ bi,<br  />\r\nLời kinh thiết tha nguyện cầu hòa chung tiếng mõ ngân vang, bến mê bờ giác khơi nguồn, trăng sao tỏa rạng thiền môn niết bàn<br  />\r\nChùa tôi dưới bóng trăng. đàn em vui múa ca, hồn nhiền tươi thắm như nụ hoa hồng nhung, Dù gian nan khó nguy dù thân tâm xót xa, lòng tôi vẫn luôn giữ vững niềm tin.<br  />\r\n<br  />\r\nDòng sông Lam uốn quanh, đồng lúa bát ngát xanh, chùa tôi như đóa sen vươn lên trời cao.<br  />\r\nNgàn hoa thơm thắm tươi hàng cây cao lá reo, chùa tôi long lanh dưới nắng chiều rơi.<br  />\r\n<br  />\r\nTiếng chuông chùa trầm lắng đem bao nguồn an vui hạnh phúc trong cõi trần ai, chùa như ánh dương soi đường, lửa thiêng sưởi ấm nơi nơi pháp luân chuyển ánh đạo vàng chúng sanh giác ngộ từ quang nơi lòng<br  />\r\nChùa tôi bao mến thương là nơi tôi lớn khôn, dạy tôi luôn sống vui chan hòa tình thương, dù cho năm tháng qua, dù cho bao đổi thay lòng tôi vẫn luôn nhớ mái chùa xưa, chùa tôi vẫn như thái dương rạng soi','',2,0,1,1,1,0),(11,'Một đạo tràng trang nghiêm<br  />\r\nMột đạo trang thanh tịnh<br  />\r\nCùng niệm A di đà<br  />\r\nDù đi đứng nằm ngồi<br  />\r\nĐều nhất tâm bất loạn<br  />\r\nLuôn niệm Adi đà<br  />\r\nCùng ngồi lại bên nhau<br  />\r\nTa niệm A di đà<br  />\r\nMột đạo tràng an vui<br  />\r\nDạy mọi người tâm thiện<br  />\r\nCùng niệm A di đà<br  />\r\nDù đang lúc làm gì<br  />\r\nĐều nhất tâm bất loạn&nbsp;<br  />\r\nLuôn niệm A di đà<br  />\r\nNgày ngày đc an vui<br  />\r\nTrong niệm A di đà<br  />\r\nNam mô a di đà phật<br  />\r\nCho chúng con vượt qua<br  />\r\nThân không mang bao tật bệnh<br  />\r\nTâm không còn ưu phiền<br  />\r\nGia quyến đc đoàn viên&nbsp;<br  />\r\nCùng quay về bờ giác<br  />\r\nSống trong niềm an lạc<br  />\r\nNgày đêm niệm Di đà','',2,0,1,1,1,0),(12,'1:<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nChấp tay quỳ kính lạy người, thiết tha con xin nguyện cầu.<br  />\r\nQuê hương là dòng sông mát trong trăng vàng soi sáng.<br  />\r\n<br  />\r\n2:<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nĐóa hoa tươi sắc vô thường, sớm khuya rơi rớt không lường.<br  />\r\nNương thuyền từ vượt qua, bến mê đến nơi bờ giác.<br  />\r\n<br  />\r\nĐK:<br  />\r\nMẹ Hiền Quán Thế Âm, giọt nước nhành dương xoá dịu lầm than.<br  />\r\nMẹ Hiền Quán Thế Âm, đem đến an vui xóa sạch ưu phiền.<br  />\r\nMẹ Hiền Quán Thế Âm, ngàn mắt ngàn tay vô lượng vô biên.<br  />\r\nMẹ Hiền Quán Thế Âm, chiếu sáng yêu thương khắp nơi bình yên.<br  />\r\n<br  />\r\n3:<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ Tát<br  />\r\nChiến tranh đau đớn cuộc đời, bão giông mây nước ngập trời.<br  />\r\nCon xin Mẹ từ bi xót thương cứu độ trần gian.<br  />\r\n<br  />\r\n<br  />\r\nChiến tranh đau đớn cuộc đời, bão giông mây nước ngập trời.<br  />\r\nCon xin Mẹ từ bi xót thương cứu độ trần gian...','',2,0,1,1,1,0),(13,'<p data-max=\"auto\" data-min=\"300px\">Một đạo tràng trang nghiêm,một đạo tràng thanh tịnh&nbsp;<br  />\r\nCùng niệm A di đà...Dù đi đứng nằm ngồi,đều nhất tâm bất loạn luôn niệm A Di Đà&nbsp;<br  />\r\nCùng ngồi lại bên nhau...Ta niệm A Di Đà!&nbsp;<br  />\r\nMột đạo tràng an vui dạy mọi người tâm thiện,cùng niệm A Di Đà&nbsp;<br  />\r\nDù đang lúc làm gì đều nhất tâm bất loạn luôn niệm a di Đà&nbsp;<br  />\r\nNgày ngày được an vui ,trong niệm A di đà...&nbsp;<br  />\r\nĐK:&nbsp;<br  />\r\nNam Mô A Di Đà Phật cho chúng con vượt qua,thân không mang bao tật bệnh tâm không&nbsp;<br  />\r\ncòn ưu phiền...gia quyến được đoàn viên cùng quay về bờ giác,sống trong niềm an lạc,ngày đêm niệm Di Đà!!&nbsp;<br  />\r\nNam Mô A Di Đà Phật cho chúng con vượt qua,thân không mang bao tật bệnh tâm không&nbsp;<br  />\r\ncòn ưu phiền...gia quyến được đoàn viên cùng quay về bờ giác,sống trong niềm an lạc,ngày đêm niệm Di Đà!</p>\r\n<br  />\r\n&nbsp;','',2,0,1,1,1,0),(14,'Phật đang trong ta từ khi biết sẽ chia niềm đau&nbsp;<br  />\r\nPhật đang trong ta từ khi biết nhiếp tâm niệm Phật&nbsp;<br  />\r\nPhật đang trong ta miệng hoan hỷ nói câu từ bi&nbsp;<br  />\r\nPhật đang trong ta bàn tay nắm với muôn bàn tay&nbsp;<br  />\r\n<br  />\r\nPhật đang trong ta từ khi ấy xóa tan sầu bi&nbsp;<br  />\r\nPhật đang trong ta từ khi ấy pháp thân an vị&nbsp;<br  />\r\nPhật đang trong ta miệng không nói đến câu thị phi&nbsp;<br  />\r\nPhật đang trong ta bằng ánh mắt chứa chan từ bi&nbsp;<br  />\r\n<br  />\r\nĐK :<br  />\r\nPhật đến chẳng mong cầu, Phật đi chớ tìm đâu, là vì lòng ta đắm mê nảo nề&nbsp;<br  />\r\nThề quyết quay trở về, dẹp tan những lầm mê, biển trần khổ đau hóa vui tịnh độ&nbsp;<br  />\r\n<br  />\r\nPhật đang trong ta đường đi tới bổng nhiên bình yên&nbsp;<br  />\r\nPhật đang trong ta bàn chân bước trở nên hành thiện&nbsp;<br  />\r\nPhật đang trong ta gặp oan trái hóa ra nhẹ tênh&nbsp;<br  />\r\nPhật đang trong ta bình yên hóa khúc ca diệu âm','',2,0,1,1,1,0),(15,'Tâm Phật là ở trong ta,&nbsp;<br  />\r\nTìm đâu ngày tháng xa hoa vô thường.<br  />\r\nYêu đương nhân thế mê lầm,&nbsp;<br  />\r\nLòng ta giải thoát chữ tâm là Phật.&nbsp;<br  />\r\nPhật là Phật ở trong tâm,&nbsp;<br  />\r\nTu tâm tu tánh phật là Phật tâm.<br  />\r\n<br  />\r\nMột nén nhang mang chút lòng thành,<br  />\r\nLạy Phật cung kính cho con vẹn toàn.<br  />\r\nMột nỗi đau ngang trái cuộc đời,&nbsp;<br  />\r\nMột lòng thương xót mới là phật tâm.<br  />\r\nChư Phật mãi ở trong ta,<br  />\r\nTu đâu cũng phải tu tâm cho mình.<br  />\r\nNam mô a di đà Phật !!!<br  />\r\nCầu cho nhân thế thân tâm an lành.<br  />\r\nNgười người cuộc sóng an vui,<br  />\r\nTu tâm dưỡng tánh Phật là Phật tâm.','',2,0,1,1,1,0),(16,'Lạy mẹ Quán Thế Âm Linh Cảm Bồ Tát, Lạy mẹ Quán Thế Âm Linh Cảm Bồ Tát<br  />\r\nThắp nén hương lòng chúng con kính dânglên mẹ từ bi<br  />\r\nLắng nghe lời chúng con phướng duyên nguyện cầu lên mẹ<br  />\r\nLạy mẹ Quán Thế Âm Linh Cảm Bồ Tát, Lạy mẹ Quán Thế Âm Linh Cảm Bồ Tát<br  />\r\nDưới bóng mẹ hiền chúng con chắp tay xem nợ từ tâm<br  />\r\nXin cứu độ chúng sanh thoát khỏi mê lầm khổ đau<br  />\r\nNam mô Đại từ Đại bi cứu khổ linh cảm ơn Quán Thế Âm Bồ Tát<br  />\r\nDưới ánh đạo vang hương tỏa ngát hướng lên đài sen chúng con nhắc tai<br  />\r\nMột lòng thành kính thiết tha xin mẹ xót thương<br  />\r\nNguyện bao nghiệp chướng tiêu trừ, nguyện bao oan vi dứt sạch pháp giới an vui rạng ngời chân lí từ bi<br  />\r\nNhành dương giọt nước cam lộ diệu hiền ánh trăng giác ngộ ,thế giới yêu thương, ngọt lành hương sác lục hoa<br  />\r\nNam mô Đại từ Đại bi Quán Thế Âm Bồ Tát,Nam mô Đại từ Đại bi Quán Thế Âm Bồ Tát<br  />\r\n<br  />\r\nLạy mẹ Quán Thế Âm Linh Cảm Bồ Tát, Lạy mẹ Quán Thế Âm Linh Cảm Bồ Tát<br  />\r\nThắp nén hương lòng chúng con kính dânglên mẹ từ bi<br  />\r\nLắng nghe lời chúng con phướng duyên nguyện cầu lên mẹ<br  />\r\nLạy mẹ Quán Thế Âm Linh Cảm Bồ Tát, Lạy mẹ Quán Thế Âm Linh Cảm Bồ Tát<br  />\r\nDưới bóng mẹ hiền chúng con chắp tay xem nợ từ tâm<br  />\r\nXin cứu độ chúng sanh thoát khỏi mê lầm khổ đau<br  />\r\nNam mô Đại từ Đại bi cứu khổ linh cảm ơn Quán Thế Âm Bồ Tát<br  />\r\nDưới ánh đạo vang hương tỏa ngát hướng lên đài sen chúng con nhắc tai<br  />\r\nMột lòng thành kính thiết tha xin mẹ xót thương<br  />\r\nNguyện bao nghiệp chướng tiêu trừ, nguyện bao oan vi dứt sạch pháp giới an vui rạng ngời chân lí từ bi<br  />\r\nNhành dương giọt nước cam lộ diệu hiền ánh trăng giác ngộ ,thế giới yêu thương, ngọt lành hương sác lục hoa<br  />\r\nNam mô Đại từ Đại bi Quán Thế Âm Bồ Tát,Nam mô Đại từ Đại bi Quán Thế Âm Bồ Tát','',2,0,1,1,1,0),(17,'Niềm an vui vẫn luôn có thật,<br  />\r\nNhư bữa cơm thanh đạm dưa, cà.<br  />\r\nGiãn đơn thôi người đừng đánh mất,&nbsp;<br  />\r\nKhỗ tâm đi tìm hình bóng đâu xa. (2 lần)<br  />\r\n<br  />\r\nPhật quanh ta ở nơi ta bà,<br  />\r\nKhi uống ăn nhớ niệm di đà.<br  />\r\nVà khi ta làm điều nhân ái,&nbsp;<br  />\r\nThấy y như là phật đến trong ta. (2 lần)<br  />\r\n<br  />\r\nĐời ta qua bao nhiêu phố phường,<br  />\r\nNhưng chỉ vui trên một con đường.<br  />\r\nĐường thanh cao dạt dào mưa pháp,<br  />\r\nSống trong muôn loài ngập nắng yêu thương. (2 lần)<br  />\r\n<br  />\r\nVề đi thôi dứt tan não phiền,&nbsp;<br  />\r\nXa nẻo mê, danh lợi, sanh tài.<br  />\r\nLàm thân tâm hận sầu tê tái,<br  />\r\nQuyết quay trở về bờ giảng an vui. (2 lần)','',2,0,1,1,1,0),(18,'Nơi cạnh núi đồi thiêng liêng<br  />\r\nmắt Mẹ đang nhìn uy nghiêm<br  />\r\nthương đàn con chịu đau thương<br  />\r\nsống trong bể khổ trầm luân<br  />\r\ncon quỳ trước Mẹ niêm hương<br  />\r\nmắt buồn giữ lệ vương vương<br  />\r\nnhắc thầm tên Mẹ yêu thương<br  />\r\nxin Mẹ chứng chi lòng con<br  />\r\nQuan Âm Mẹ hiền<br  />\r\nxin Mẹ thương chúng con<br  />\r\ngiọt nước cành dương<br  />\r\nxóa sạch bao ưu phiền<br  />\r\nNam Mô Mẫu Từ<br  />\r\ntay Mẹ thường đón đưa<br  />\r\nvạn kiếp trần lao<br  />\r\nMẹ đã dấn thân vào<br  />\r\ncon đã quay về nơi đây<br  />\r\nhương từ chiếu tỏa mây bay<br  />\r\nmuôn lòng ấm lại hôm nay<br  />\r\nnhánh dương cứu khổ từ đây','',2,0,1,1,1,0),(19,'Xin cho con một niềm tin<br  />\r\nTin vào chính bản thân mình<br  />\r\nKhông phạm vào những điều răn<br  />\r\nHằng ngày sám hối ăn năn.<br  />\r\n<br  />\r\nXin cho con một lòng nhân<br  />\r\nTình thương đem đến xa gần<br  />\r\nNhìn bằng ánh mắt từ bi<br  />\r\nTrải rộng tấm lòng vị tha.<br  />\r\n<br  />\r\nXin cho con một con đường<br  />\r\nĐi vào nẻo chánh đường ngay<br  />\r\nĐể con tu tập thường ngày<br  />\r\nThân tâm an lạc từ đây.<br  />\r\n<br  />\r\nXin cho mọi người như con<br  />\r\nTình yêu thương hãy song còn<br  />\r\nDẹp hận thù đến với nhau<br  />\r\nCùng tu hành theo chánh đạo','',2,0,1,1,1,0),(20,'Lạy Đấng Tam Giới Tôn quy mạng cùng mười phương Phật<br  />\r\nNay con phát nguyện rộng, thọ trì Kinh Pháp Hoa.<br  />\r\nLời Kinh bay cao lời kinh lan tỏa nhiệm màu<br  />\r\nLời Kinh đưa ta về cõi phúc, xa lánh chốn địa ngục<br  />\r\nCho con thấy Ánh Đạo Vàng.<br  />\r\n<br  />\r\n&#91;ĐK:&#93;<br  />\r\n<br  />\r\nDiệu Pháp Liên Hoa vô biên nghĩa nhiệm màu<br  />\r\nNguyện hiểu nghĩa chân thật<br  />\r\nCủa Phật Như Lai, của Phật Như Lai.<br  />\r\nDiệu Pháp Liên Hoa cho ta nước cam lồ<br  />\r\nÔi để hồn dịu mát sen hồng thơm ngát trần gian<br  />\r\nSen hồng tỏa ánh hào quang.<br  />\r\n<br  />\r\nLạy Đấng Tam Giới Tôn quy mạng cùng mười phương Phật<br  />\r\nNay con phát nguyện rộng, thọ trì kinh Pháp Hoa.<br  />\r\nĐời con như mây nhẹ bay trên cõi hồng trần<br  />\r\nNgày đêm luôn kinh cầu Diệu Pháp<br  />\r\nCho dẫu nhiều lỗi lầm câu Kinh xóa hết tội tình.','',2,0,1,1,1,0),(21,'Xin cho con một niềm tin<br  />\r\nTin vào chính bản thân mình<br  />\r\nKhông phạm vào những điều răn<br  />\r\nHằng ngày sám hối ăn năn.<br  />\r\n<br  />\r\nXin cho con một lòng nhân<br  />\r\nTình thương đem đến xa gần<br  />\r\nNhìn bằng ánh mắt từ bi<br  />\r\nTrải rộng tấm lòng vị tha.<br  />\r\n<br  />\r\nXin cho con một con đường<br  />\r\nĐi vào nẻo chánh đường ngay<br  />\r\nĐể con tu tập thường ngày<br  />\r\nThân tâm an lạc từ đây.<br  />\r\n<br  />\r\nXin cho mọi người như con<br  />\r\nTình yêu thương hãy song còn<br  />\r\nDẹp hận thù đến với nhau<br  />\r\nCùng tu hành theo chánh đạo','',2,0,1,1,1,0),(22,'Ai bảo ăn chay là khổ. Ăn chay vui lắm chứ ( ăn chay vui lắm chứ). Vì từ bi , tấm lòng vị tha. Vì thương sót chúng sinh, không lấy xác sinh làm vui. Mà dùng chay thanh khiết, bát cơm gạo ta chấm tương chao cơm trắng vào thật mau (Cơm trắng vào thật mau). Này cơm trắng ngần bay mùi thơm ngất ngây. Lòng mang ân dị bác nông phu cấy cày. Này rau xanh màu mưa nắng tháng ngày ra công. Muôn loài vui sống<br  />\r\nkhông lo ận thù ( muôn loài vui sống không lo ận thù)<br  />\r\nAi muốn cho tâm thanh nhàn thì ăn chay đi sẽ biết ( ăn chay đi sẽ biết). Lòng thảnh thơi mặt đời thay đổi, đường đi đi ta cứ đi. Đã quyết noi gương từ bi thì bật lòng chi ân oán. Nếu gieo nhân ai cãi vô cương sao cái lành quật cây ( sao cái lành quật cây). Đừng mong đến già khi tuổi xuân sớm qua.<br  />\r\nCùng gieo duyên lành nếu tâm qua về. Về gương bóng phật gương pháp đối kề gương tâm. Nương nhờ tam bảo ,hoa sen nở bừng( nương nhờ tam bảo, hoa sen nỡ bừng) la ...lá...la...lá...la...la...là...là. ...lá . lá ... lá ... la ... la... là... là... la. La... lá... la... lá ... là ... là .... la','',2,0,1,1,1,0),(23,'Hạnh phúc thay đức phật ra đời<br  />\r\nTỳ Ny Vệ Hạ Vô Ưu ná<br  />\r\nMa Da Hoàng Hậu thái tử sanh<br  />\r\nTinh phạn vương vui mừng khôn xiết<br  />\r\nCác tỳ la vê rồng đáp ngộ&nbsp;<br  />\r\nThiên nữ ngũ công trỗi nhạc trời<br  />\r\nTrăng hoa ngàn dãi cánh trăng ngời<br  />\r\nBốn phương ba cõi đón như lai<br  />\r\nẤn đồ Việt Nam như lai hiện<br  />\r\nViệt Nam Ân độ hiện như lai,&nbsp;<br  />\r\nHóa thân bồ tác hộ minh giám,<br  />\r\nĐảng sinh vị hiện bật từ bi.&nbsp;<br  />\r\nHạnh phúc hạnh phúc mừng thế tôn,&nbsp;<br  />\r\nChấp tay quy hướng đón ánh đào,&nbsp;<br  />\r\nNam nữ gái trai dâng hoa tình,<br  />\r\nGià trẻ lớn bé... hát mừng vui.....<br  />\r\n<br  />\r\nĐêm nay tháng 4 mừng khánh đàng,&nbsp;<br  />\r\nNghèo khó bần hàn đã đổi thay,&nbsp;<br  />\r\nChiến tranh đau khổ không còn nữa,<br  />\r\nHạnh phúc mừng vui phật ra đời……………<br  />\r\nPhật ơi phật con mến phật.<br  />\r\nNgười ra đời rồi cứu chúng con,<br  />\r\nKhổ đâu tan biến bông hoa kia,&nbsp;<br  />\r\nHạnh phúc mừng vui phật ra đời.<br  />\r\nẤn đồ Việt Nam như lai hiện<br  />\r\nViệt Nam ấn độ hiện như lai,&nbsp;<br  />\r\nHóa thân bồ tác hộ minh giám,<br  />\r\nĐảng sinh vị hiện bật từ bi.&nbsp;<br  />\r\n<br  />\r\nHạnh phúc hạnh phúc mừng thế tôn,&nbsp;<br  />\r\nChấp tay quy hướng đón ánh đào,&nbsp;<br  />\r\nNam nữ gái trai dâng hoa tình,<br  />\r\nGià trẻ lớn bé..... hát mừng vui......<br  />\r\n<br  />\r\nHạnh phúc hạnh phúc mừng thế tôn,&nbsp;<br  />\r\nChấp tay quy hướng đón ánh đào,&nbsp;<br  />\r\nNam nữ gái trai dâng hoa tình,<br  />\r\nGià trẻ lớn bé.... hát mừng vui......','',2,0,1,1,1,0),(24,'Kể chuyện Phật Thích Ca ra đời, ngày xưa.<br  />\r\nVào ngày rằm tháng tư nơi Lâm Tỳ Ni.<br  />\r\nHoàng hậu dựa gốc cây vương ưu đẹp tươi<br  />\r\nChợt hòa vô ưu tưng bừng nở<br  />\r\n<br  />\r\nĐiềm lành đã đến cho muôn loài từ nay<br  />\r\nThật là hạnh phúc cho nhân loại mừng thay<br  />\r\nRạng ngời ngày Đản Sanh Đức Phật từ bi<br  />\r\nNgày bình an đến cùng thế gian<br  />\r\n<br  />\r\nNgài thấy vô minh trần đời<br  />\r\nNgài thấy đau thương mọi loài<br  />\r\nNgài xuất gia đi vào rừng<br  />\r\nVà tìm ra ánh đạo thiên<br  />\r\n<br  />\r\nChân lí cao siêu từ bi<br  />\r\nSoi sáng vô minh lầm mê<br  />\r\nTử sinh luân hồi tận<br  />\r\nBến mơ không còn bờ<br  />\r\nĐộ đời vượt hết mọi khổ đau<br  />\r\n<br  />\r\nMừng ngày Phật Thích Ca ra đời, ngày nay<br  />\r\nLòng thầm nguyện ước cho muôn loài, bình an<br  />\r\nTâm hồn bừng sáng tươi như ưu đàm hoa<br  />\r\nVà từ tâm ấy Phật ra đời.<br  />\r\nTâm hồn bừng sáng tươi như ưu đàm hoa<br  />\r\nVà từ tâm ấy Phật ra đời.','',2,0,1,1,1,0),(25,'Hôm nay gieo hạt giống,&nbsp;<br  />\r\ntương lai gặt hoa trái.<br  />\r\nHôm nay ý nghĩ lành,&nbsp;<br  />\r\nmiệng nói những lời lành,&nbsp;<br  />\r\nthân làm các việc lành<br  />\r\nQuả lành sẽ theo ta&nbsp;<br  />\r\nnhư bóng đi theo hình<br  />\r\nHình ngay bóng thẳng ngay&nbsp;<br  />\r\n<br  />\r\nHôm nay ý nghĩ xấu,&nbsp;<br  />\r\nmiếng nói những lời xấu,&nbsp;<br  />\r\nthân làm các việc xấu<br  />\r\nQuả xấu sẽ theo ta&nbsp;<br  />\r\nnhư bánh lăn theo xe<br  />\r\nXe đâu bánh theo đó<br  />\r\nThiện ác đều có báo<br  />\r\nNhân quả luật không sai<br  />\r\n<br  />\r\nTương lai của đời ta,&nbsp;<br  />\r\nkhổ đau hay hạnh phúc,&nbsp;<br  />\r\nngày nay ta quyết định<br  />\r\nHãy ươm hạt giống lành,&nbsp;<br  />\r\nchăm sóc và dưỡng nuôi<br  />\r\nHoa hạnh phúc sẽ nở,&nbsp;<br  />\r\nquả an vui hiện tiền<br  />\r\n<br  />\r\nNày bạn thân yêu ơi,&nbsp;<br  />\r\nhãy ươm hạt giống lành,&nbsp;<br  />\r\nchăm sóc vào dưỡng nuôi<br  />\r\nCuộc đời sẽ thơm hoa,&nbsp;<br  />\r\nan lạc sẽ theo ta&nbsp;<br  />\r\nhôm nay và mai sau<br  />\r\n...','',2,0,1,1,1,0),(26,'thân thương chiếc áo màu lam&nbsp;<br  />\r\nmặc vào người thây tánh tham tan dần<br  />\r\nan nhiên dang dến thật gần&nbsp;<br  />\r\nthảm si ,sân ,hận ,lần lần ra ik&nbsp;<br  />\r\nngôi chùa dầm ấm khắc ghi<br  />\r\nthiện duyên đã đến con về chùa&nbsp;<br  />\r\nthầy hiền ,bạn tốt,thi đua<br  />\r\nđêm ngày học pháp bốn mùa an vui&nbsp;<br  />\r\n*****<br  />\r\nơi thân thương áo lam hiền&nbsp;<br  />\r\nvề miền đồng sâu ,núi cao&nbsp;<br  />\r\nem đem chia sớt cho người&nbsp;<br  />\r\nhạt gạo hay tấm áo<br  />\r\nơi thân thương áo lam hiền&nbsp;<br  />\r\nvì lòng từ bi hiến dâng&nbsp;<br  />\r\nnghe bâng khuâng chút&nbsp;<br  />\r\nnắng nhạt ,vừa cợt đùa vạt áo em','',2,0,1,1,1,0),(27,'Ông Di Lặc cười hay ha<br  />\r\nÔng luôn ở gần ta<br  />\r\nMang trong lòng niềm thương yêu<br  />\r\nCứu nhân giúp thế an lành<br  />\r\n<br  />\r\nÔng thương nhiều con nít lắm<br  />\r\nTay bồng tay bế mà vui<br  />\r\nGia đình ai không đầm ấm<br  />\r\nRước ông về, sẽ thuận hòa vui<br  />\r\n<br  />\r\nÔng Di Lặc là Phật linh<br  />\r\nGiúp mọi người an khang<br  />\r\nÔng Di Lặc bụng to rất to<br  />\r\nKhỏi lo đói hay nghèo<br  />\r\n<br  />\r\nÔng Di Lặc là tương lai<br  />\r\nMang niềm may mắn ngày mai<br  />\r\nMang túi phép bên người<br  />\r\nÔng tươi cười cứu giúp cho đời<br  />\r\n<br  />\r\nĐK:<br  />\r\n<br  />\r\nÔng Di Lặc là Phật linh<br  />\r\nDấn thân giữa trần gian<br  />\r\nMang cho đời một ngày mai<br  />\r\nTương lai mãi bình an<br  />\r\n<br  />\r\nAi xuôi ngược nơi tha phương<br  />\r\nHay là không có tình thương<br  />\r\nHãy nhớ đến ông Di Lặc<br  />\r\nRồi sẽ thấy yêu đời<br  />\r\n<br  />\r\n(*)<br  />\r\nNam mô Di Lặc Phật! Ngài hoan hỉ và từ bi<br  />\r\nNam mô Di Lặc Phật! Ngài ban phước khắp trần gian','',2,0,1,1,1,0),(28,'Nam Mô Đại Từ Đại Bi Quán Thế Âm Bồ-tát!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNam Mô Đại Từ Đại Bi Quán Thế Âm Bồ-tát!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNam Mô … Đại Từ Đại Bi…tai lắng nghe nỗi đau trần gian!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNam Mô Đại Từ Đại Bi … mắt thương … cuộc đời!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nAi lạc loài u tối?&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nAi ngụp sâu không lối?&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cho ánh dương lên bừng soi!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cho đớn đau mau dần vơi.&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cùng chúng sanh khắp mọi nơi!&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm thường ấm êm… cõi lòng.&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nAi chìm trong giông bão?&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nAi ngập trong biển lửa?&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cho gió mưa thôi cuồng quay.&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cho lửa sân vơi tàn mau.&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm cùng chúng sanh khắp mọi nơi.&nbsp;<br  />\r\n<br  />\r\n<br  />\r\nNiệm danh Quán Thế Âm thường có…bên… Mẹ hiền.','',2,0,1,1,1,0),(29,'Lời 1:<br  />\r\nHoàng hôn minh chiếu khắp mười phương<br  />\r\nTà áo chen bay sắc dịu thường<br  />\r\nTrầm hương tỏa ngát mừng cha lành<br  />\r\nKết đóa liên đài tôn kính dâng.<br  />\r\n<br  />\r\nLời 2:<br  />\r\nTrần gian cùng đón đức từ tôn<br  />\r\nMuôn đóa hoa tâm thành cúng dường<br  />\r\nĐài sen thanh thoát tâm thế Phật<br  />\r\nDang cánh tay cùng tìm độ người<br  />\r\n<br  />\r\nĐK:<br  />\r\nNgàn muôn lần quý kính, xin dâng hết tâm hương<br  />\r\nDâng muôn đóa hoa hiền, xin thanh bình bà con<br  />\r\nNgàn muôn lần quý ngưỡng, xin ánh sáng quang minh<br  />\r\nXin mưa pháo nơi nơi, xin thắm nhuận cõi tâm<br  />\r\n<br  />\r\nLời 3:<br  />\r\nĐường lên anh đức đất từ tôn<br  />\r\nHoàng hĩ năm châu ra chí thần<br  />\r\nHồng anh tỏa sáng ngày vía phật<br  />\r\nSang cõi ta bà như lạc bay','',2,0,1,1,1,0),(30,'Nay hè sang chim líu lo sau vườn.<br  />\r\nVe hòa vang trên nhánh cây bên đường.<br  />\r\nXIN mẹ cha em từ giã ngôi nhà mái trường.<br  />\r\nEm thật xinh trong áo lam dịu dàng.&nbsp;<br  />\r\nTrong bình minh em bước đi nhẹ nhàng.&nbsp;<br  />\r\nMôi cười tươi đón hè tới tình thương chứa chan.&nbsp;<br  />\r\nEm công phu thật lâu.&nbsp;<br  />\r\nTan biến mau nỗi đau mê lầm.<br  />\r\nCho tham lam sân hận chìm dần vào quên lãng.&nbsp;<br  />\r\nTrong con tim từ lâu em khắc sâu ước mơ xa vờ.<br  />\r\nTham đua tranh danh lợi gây khổ đau cho người.&nbsp;<br  />\r\nAn lành thay em sớm hôm học hành.&nbsp;<br  />\r\nAn lành thay em có thêm bạn lành.&nbsp;<br  />\r\nAn lành thay nơi đời sống ta bà giả tạm.&nbsp;<br  />\r\nNương hồng danh em chấp tay kinh hành, tâm tịnh thanh em sống trong an lành.&nbsp;<br  />\r\nƠn thầy tổ em nguyện đáp học tu tiến lên','',2,0,1,1,1,0),(31,'Tịng Quang buồn<br  />\r\nTừ hòa tiến tôi về,thăm ngôi chùa Tịnh Quang với hàng cây bồ đề lẫn trong niềm ngoại ô ,xa đô thành nghên náo chùa mang tên (Tịnh Quang).<br  />\r\nChim reo quanh năm cây nhiều hoa trái cho đất phật thêm nở hoa.Tôi nghe gió ru cây,chuông vang ngân dài thêm lòng rưng rưng khắc khỏi chìm giữa đêm Tịnh Quang buồn.<br  />\r\nĐK:<br  />\r\nGói gấm uy nghi đứng nhìn ra góc như đón chào đàn con xa,chuông vang ngân nga, ru lòng nhân thế mau giác ngộ xa bến mê,đi sau vô trong Thầy Thượng Tâm ngồi đó đưa mắt nhìn xem thế nhân,bao nhiêu đâu thương khổ đâu vùi lấp khi gối đầu dâng nén hương.<br  />\r\nMột lần đến nơi này đi viếng chùa cùng sư, mà tôi thấy như hoài cũng con đường này đây,cũng ngôi chùa này đây còn mang kỷ niệm đầy.<br  />\r\nMai đây ra đi mang nhiều nhung nhớ cho những ngày tu đã qua&nbsp;<br  />\r\n(tôi nghe gió ru cây chim kêu vang ngoài hiên nhìn không gian vắng lặng càng thấy thương Thầy nhiều)2 lần','',2,0,1,1,1,0),(32,'Dòng A Nô Ma sóng nhấp nhô bờ lau xanh.<br  />\r\nNhìn làn nước biếc, Thích Ca ngài lòng vững bền.<br  />\r\nThôi con hãy về để ta vui ánh vàng.<br  />\r\nTa đã quyết tìm đạo sáng cứu chúng sanh.<br  />\r\nA Nô Ma vẫn còn nhớ ghi gương sáng ngời.<br  />\r\nChúng ta giờ đây nguyện theo đức từ bi.<br  />\r\nThành Ca Tỳ La sống yên vui thời Tịnh Vương.<br  />\r\nNgười người vui sướng Thích Ca ngài vừa ra đời.<br  />\r\nMuôn chim hát mừng lá hoa hương ngát ngào.<br  />\r\nMuôn hào quang ngời rọi chiếu khắp núi sông,<br  />\r\nCây Vô ưu đến này chúng sinh luôn nhắc hoài.<br  />\r\nNơi Lâm Tỳ Ni còn ghi bao ngày vui.','',2,0,1,1,1,0),(33,'Tâm Phật là ở trong ta,&nbsp;<br  />\r\nTìm đâu ngày tháng xa hoa vô thường.<br  />\r\nYêu đương nhân thế mê lầm,&nbsp;<br  />\r\nLòng ta giải thoát chữ tâm là Phật.&nbsp;<br  />\r\nPhật là Phật ở trong tâm,&nbsp;<br  />\r\nTu tâm tu tánh phật là Phật tâm.<br  />\r\n<br  />\r\nMột nén nhang mang chút lòng thành,<br  />\r\nLạy Phật cung kính cho con vẹn toàn.<br  />\r\nMột nỗi đau ngang trái cuộc đời,&nbsp;<br  />\r\nMột lòng thương xót mới là phật tâm.<br  />\r\nChư Phật mãi ở trong ta,<br  />\r\nTu đâu cũng phải tu tâm cho mình.<br  />\r\nNam mô a di đà Phật !!!<br  />\r\nCầu cho nhân thế thân tâm an lành.<br  />\r\nNgười người cuộc sóng an vui,<br  />\r\nTu tâm dưỡng tánh Phật là Phật tâm.','',2,0,1,1,1,0),(34,'<br  />\r\nNghe tiếng võng trưa buồn tôi nhớ lại ngày xưa<br  />\r\nLúc tuổi thơ con bồng bế mẹ ru<br  />\r\nCạnh bờ tre gió thổi rì rào<br  />\r\nCon sông quê êm đềm nằm soi bóng nắng cao cao<br  />\r\n&nbsp;<br  />\r\nThương nhớ những cánh cò trong tiếng mẹ buồn ru<br  />\r\nBướm rủ nhau về đầu nhánh mù u<br  />\r\nNằm nhìn ra lúa trổ đòng đòng<br  />\r\nÔm con thơ trong lòng mẹ buông câu hát ầu ơ<br  />\r\n&nbsp;<br  />\r\nDí dầu cầu ván đong đưa<br  />\r\nCầu tre lắt lẻo gập gềnh khó đi<br  />\r\nKhó đi mẹ dắt con đi<br  />\r\nCon đi trường học mẹ đi trường đời<br  />\r\n&nbsp;<br  />\r\n&nbsp;','',2,0,1,1,1,0),(35,'<br  />\r\nPhật về mở cửa vô minh<br  />\r\nKhơi dòng suối mát nối tình chúng sanh<br  />\r\nPhật về cho đất thêm lành<br  />\r\nCho hoa thêm nhụy cho cành trổ bông<br  />\r\nPhật về con cá ra sông<br  />\r\nChim về tổ ấm mát đồng lúa non<br  />\r\nPhật về an trú trong lòng<br  />\r\nTham si sân hận không còn chỗ sanh<br  />\r\nPhật về cho hết chiến tranh<br  />\r\nHoa sen nở khắp đất lành năm châu<br  />\r\nPhật về vui hết khổ đau<br  />\r\nĐông tây gần lại tình sâu đạo mầu<br  />\r\nPhật về thế giới hoan ca<br  />\r\nSông dài núi biếc ấy nguồn cội ta<br  />\r\nPhật về tươi sáng muôn nhà<br  />\r\nVui câu niệm Phật thoát vòng tử sanh.','',2,0,1,1,1,0),(36,'Xin cho con,&nbsp;<br  />\r\nĐược về bên Mẹ.&nbsp;<br  />\r\nNơi bình an chan chứa.&nbsp;<br  />\r\nXin cho con,&nbsp;<br  />\r\nĐược về với Mẹ,&nbsp;<br  />\r\nNơi mái ấm yêu thương.&nbsp;<br  />\r\nMẹ luôn ủi an những ngày,&nbsp;<br  />\r\nĐắm trong hoài nghi,&nbsp;<br  />\r\nLo buồn mất mác.&nbsp;<br  />\r\nMẹ ban cho con nụ cười,&nbsp;<br  />\r\nNgày con chợt thấy,&nbsp;<br  />\r\nTình yêu vụt mất.&nbsp;<br  />\r\nĐK:&nbsp;<br  />\r\nDù cho, chính con mang tội này&nbsp;<br  />\r\nDù cho, bước chân con ngại ngần&nbsp;<br  />\r\nĐừng xa lánh con,&nbsp;<br  />\r\nĐừng chê trách con,&nbsp;<br  />\r\nĐừng quên lãng con Mẹ nhé.&nbsp;<br  />\r\nĐừng xa lánh con,&nbsp;<br  />\r\nĐừng chê trách con,&nbsp;<br  />\r\nĐừng quên lãng con Mẹ yêu...&nbsp;<br  />\r\nNhiều khi, trái tim con nguội lạnh&nbsp;<br  />\r\nNhiều khi, đức tin con nhạt nhòa&nbsp;<br  />\r\nĐừng xa lánh con,&nbsp;<br  />\r\nĐừng chê trách con,&nbsp;<br  />\r\nĐừng quên lãng con Mẹ nhé.&nbsp;<br  />\r\nĐừng xa lánh con,&nbsp;<br  />\r\nĐừng chê trách con,&nbsp;<br  />\r\nĐừng quên lãng con, Mẹ yêu...','',2,0,1,1,1,0),(37,'Mẹ bên đàn con sum vầy sống trên đất lành<br  />\r\nMẹ lo ngày con biếng ăn mẹ mong bao tình thương<br  />\r\nTrời khi sấm sét âm u mẹ ôm con trong lòng<br  />\r\nMẹ giúp cho đàn bé thơ trong đời bước đi gặp khó<br  />\r\n&nbsp;<br  />\r\nMẹ bên đàn con như dòng suối trong ngọt ngào<br  />\r\nMẹ thương đàn con thiết tha tựa biển trời bao la<br  />\r\nNgàn năm tiếng hát vang xa mẹ mến thương dịu hiền<br  />\r\nMẹ chắt chiu ngày tháng qua nuôi đàn các con dại thơ<br  />\r\n&nbsp;<br  />\r\nTheo cội nguồn bước đi trong đời công ơn mẹ ngất cao ngang trời<br  />\r\nAi đong đầy nước sông ngang bằng sức công của người mẹ quý<br  />\r\nMang trong lòng hát ca người mẹ như trong nguồn nước ra tuôn tràn<br  />\r\nCon reo mình thấy ba mẹ cùng các con sống vui êm đềm<br  />\r\n&nbsp;<br  />\r\nMẹ bên đàn con như hàng bóng cây trên đường<br  />\r\nMẹ bên đàn con như hàng bóng cây trở che<br  />\r\nDù mưa nắng tiết đông sang mẹ vững tâm vững bền<br  />\r\nMột thoáng qua một lớn khôn vui nhìn thấy con thành nhân<br  />\r\n&nbsp;','',2,0,1,1,1,0),(38,'Kính lạy bồ tát Viên Dung&nbsp;<br  />\r\nlòng từ ban trải&nbsp;<br  />\r\nkhông cùng độ sanh<br  />\r\nBồ tát là người mẹ&nbsp;<br  />\r\ntrên tình mẹ<br  />\r\nđã bao nhiêu đời&nbsp;<br  />\r\nbao nhiêu kiếp thề nguyện&nbsp;<br  />\r\nkhông về ngôi chánh giác&nbsp;<br  />\r\nkhi chúng sanh còn lạc loài<br  />\r\nđau khổ điêu linh<br  />\r\n<br  />\r\nKính lạy bồ tát Viên Dung&nbsp;<br  />\r\nlòng từ ban trải&nbsp;<br  />\r\nkhông cùng độ sanh<br  />\r\nKính lạy bồ tát tịnh danh<br  />\r\nMười hai lời nguyện&nbsp;<br  />\r\nkết thành đài sen<br  />\r\nhoa đất sáng tỏa đêm đen&nbsp;<br  />\r\nlung linh ngọn nến&nbsp;<br  />\r\nkết đèn chân như<br  />\r\nmẹ trên tình mẹ lòng từ<br  />\r\nđã bao nhiêu kiếp&nbsp;<br  />\r\nchở che cho đời<br  />\r\nbiển trần biển lệ đầy vơi<br  />\r\ngiông thuyền bát nhã&nbsp;<br  />\r\nđộ người lầm than<br  />\r\n<br  />\r\ncứu chúng sanh hết tai nạn<br  />\r\ntrong cơn lửa dữ&nbsp;<br  />\r\nbình an chở về<br  />\r\ndù người mắc nạn sơn khê<br  />\r\nbuông thân hóa hiện&nbsp;<br  />\r\ntứ bề lo toan<br  />\r\n<br  />\r\nkính lạy mẹ thánh kim cang<br  />\r\nđộ sanh độ tứ&nbsp;<br  />\r\ntrên ngàn nẻo xa<br  />\r\nmẹ về đây cõi tát đà<br  />\r\ncành dương thấm đất&nbsp;<br  />\r\nnở hoa chân thường<br  />\r\nmuôn hoa xin góp cúng dường<br  />\r\ncầu xin thế giới&nbsp;<br  />\r\nthanh bình nơi nơi<br  />\r\nmuôn hoa xin góp cúng dường<br  />\r\ncầu xin thế giới&nbsp;<br  />\r\nthanh bình nơi nơi<br  />\r\nmuôn hoa xin góp cúng dường<br  />\r\ncầu xin thế giới&nbsp;<br  />\r\nthanh bình nơi nơi','',2,0,1,1,1,0),(39,'<br  />\r\nCó một đêm con mơ về dưới chân mẹ nũng nịu ngày thơ như đứa bé lên ba<br  />\r\nGiữa trưa hè dưới hàng tre rợp mát nằm lim dim nghe mẹ hát ầu ơ<br  />\r\nCó một đêm nhớ quay quắt về cha thèm câu răn đe ậm ừ nghiêm nghị<br  />\r\nCha cho con niềm tin và ý trí rộng lượng khi khiêm nhường không ích kỉ hão danh<br  />\r\nCó một đêm muốn trò chuyện cùng em bên tách trà thơm trải niềm tâm sự<br  />\r\nKể hết buồn vui và những gì do dự giữa biển đời chênh vênh<br  />\r\nCó một đêm tôi ngồi thật bình yên thấy phật vô biên thấy đời hữu hạn<br  />\r\n&nbsp;<br  />\r\nNiềm vui nơi đâu chợt tràn về lênh lai lòng tôi thênh thang như biển ngàn vô phiền<br  />\r\n&nbsp;','',2,0,1,1,1,0),(40,'Một ngày về thăm quê mẹ<br  />\r\nKhẽ nghe tiếng võng nhớ mẹ ầu ơ<br  />\r\nMẹ nhai cơm đút cháo thơm<br  />\r\nCon liền nhớ mẹ từ thủa nuôi con<br  />\r\nCon ơi ngoan giỏi nghe con<br  />\r\nThủa mẹ ầu ơ ru con mau ngủ<br  />\r\nDễ thương êm ả tiếng ru<br  />\r\nGiấc mơ thần tiên thiên thu nhớ mẹ<br  />\r\nẦu ơ ầu ơ chim chuyển về núi tối rồi<br  />\r\nChị em toan liệu lấy nồi nấu cơm<br  />\r\nNấu cơm chưa chín chạy lại nấu canh<br  />\r\nBỏ tiêu cho ngọt bỏ hành cho thơm<br  />\r\nẦu ơ ầu ơ trăng rằm mười sáu trăng treo<br  />\r\nChị em tôi nghèo dệt vải bán bông<br  />\r\nẦu ơ ầu ơi chim quyên ăn trái nhãn lồng<br  />\r\nTrái ăn t